n0:createOracleTrigger.n0:source:
  CREATE OR REPLACE EDITIONABLE TRIGGER "DEMO"."UXI_BI_AUTHN_METHODS" 
  before insert on "UXI_AUTHN_METHODS"               
  for each row  
begin   
  if :NEW."ID" is null then 
    select "AUTHN_METHODS_SEQ".nextval into :NEW."ID" from sys.dual; 
  end if; 
end; 

/
/
ALTER TRIGGER "DEMO"."UXI_BI_AUTHN_METHODS" ENABLE;
